export const getModuleById = (id) => {
  return modules.find(module => module.id === parseInt(id));
};

export const getChapterById = (moduleId, chapterId) => {
  const module = modules.find(m => m.id === parseInt(moduleId));
  if (module) {
    return module.chapters.find(chapter => chapter.id === parseInt(chapterId));
  }
  return null;
};


